import React from "react";
import styled from "styled-components";

import ximage from "media/image/X.png";
import { Logo } from "media/svg/Svg";
import Category from "./Category";

// 메뉴 버튼 열었을 때
const Menu = () => {
  return (
    <React.Fragment>
      <Wrap>
        <RowWrap>
          <CancleButton />

          <LogoWrapper>
            <Logo />
          </LogoWrapper>
        </RowWrap>
        <Category1>Trils</Category1>

        <Category2>Trilog</Category2>
      </Wrap>
    </React.Fragment>
  );
};

export default Menu;

const Wrap = styled.div`
  display: none;
  @media (max-width: 540px) {
    display: flex;
    flex-direction: column;
    width: auto;
    padding: 1rem;
  }
`;

const RowWrap = styled.div`
  @media (max-width: 540px) {
    display: flex;
    flex-direction: row;
    width: auto;
  }
`;

const CancleButton = styled.div`
  width: 1rem;
  height: 1rem;
  background-image: url(${ximage});
  background-size: cover;
`;

const LogoWrapper = styled.div`
  margin-left: 12.5rem;
  cursor: pointer;
  & svg {
    width: 6rem;
  }
`;

const Category1 = styled.a`
  cursor: pointer;
  color: #2b61e1;
  margin-right: 4rem;
  opacity: 0.7;
  padding: 2rem;

  :hover {
    opacity: 1;
  }
`;

const Category2 = styled.a`
  cursor: pointer;
  color: #2b61e1;
  opacity: 0.7;
  padding: 2rem;

  :hover {
    opacity: 1;
  }
`;

const Br = styled.div`
  width: 100%;
  height: 0.2rem;
  background-color: #2b61e1;
`;
