import React, { useRef, useState, useEffect } from "react";
import styled from "styled-components";
import Hls from "hls.js";
import ProgressBar from "./ProgressBar";
import { HeartEmpty, HeartFill } from "media/svg/Svg";
import { useDispatch } from "react-redux";
import { TrilsActions } from "redux/modules/trils";
import Swal from "sweetalert2";

const Videom3u8 = (props) => {
  const { mr, history } = props;
  const hls = new Hls();
  const player = useRef(null);
  const [ismuted, setMute] = useState(true);
  const [completed, setCompleted] = useState(0);
  const dispatch = useDispatch();

  const params = {
    src: props.information.videoUrl,
  };

  const heart = () => {
    dispatch();
  };

  useEffect(() => {
    if (hls === undefined) {
      return;
    }
    if (Hls.isSupported()) {
      hls.attachMedia(player.current);
      hls.config.debug = false;

      hls.on(Hls.Events.MEDIA_ATTACHED, (event, data) => {
        hls.loadSource(params.src);
      });
      hls.on(Hls.Events.ERROR, (event, data) => {
        if (data.fatal) {
          switch (data.type) {
            case Hls.ErrorTypes.NETWORK_ERROR:
              console.log("네트워크 오류");
              hls.startLoad();
              break;
            case Hls.ErrorTypes.MEDIA_ERROR:
              console.log("미디어 오류");
              hls.recoverMediaError();
              break;
            default:
              hls.destroy();
              break;
          }
        }
      });

      return () => hls.destroy();
    } else if (player.current.canPlayType("application/vnd.apple.mpegurl")) {
      player.current.src = params.src;
      player.current.addEventListener("loadedmetadata", () => {
        player.current.play();
      });
    }
  }, [params.src]);

  const videoplay = () => {
    if (player.current.readyState !== 4) {
      return;
    }
    player.current.play();
  };

  const videopause = () => {
    if (player.current.readyState !== 4) {
      return;
    }
    player.current.pause();
  };

  const openModal = () => {
    dispatch(TrilsActions.getPostDetail(props.information.id));
  };

  const like = (e) => {
    e.stopPropagation();
    const access_token = localStorage.getItem("access_token");
    if (!access_token) {
      Swal.fire({
        title: "로그인을 해주세요.",
        text: "로그인 후 좋아요를 누를 수 있습니다.",
        icon: "warning",
        showCancelButton: true,
        confirmButtonColor: "#3085d6",
        cancelButtonColor: "#d33",
        confirmButtonText: "로그인하기",
        cancelButtonText: "닫기",
      }).then((result) => {
        if (result.isConfirmed) {
          history.push("/login");
        }
      });
    }
    dispatch(TrilsActions.send_like(props.information.id, props.member.isLike));
  };

  return (
    <VideoCards margin={mr} onClick={openModal}>
      <Profile>
        <ProfileImg src={props.author.profileImgUrl} />
        <ProfileId>{props.author.nickname}</ProfileId>
      </Profile>
      <VideoPlay
        onMouseOver={videoplay}
        onMouseLeave={videopause}
        ref={player}
        muted={ismuted}
        loop
        onTimeUpdate={() => {
          setCompleted(
            (player.current.currentTime / player.current.duration) * 100
          );
        }}
      />
      {/* <VideoBg /> */}
      <ProgressBar bgcolor={"#6a1b9a"} completed={completed} />
      <BottomCov>
        <LikeCov onClick={like}>
          {props.member.isLike ? <HeartFill /> : <HeartEmpty />}
        </LikeCov>
      </BottomCov>
      <PostBottom>
        <PostLikeCnt>좋아요 +{props.information.likeNum}</PostLikeCnt>
        <PostUserComment>
          {props.information.hashtag.map((p, idx) => {
            return (
              <>
                <Hash>#{p}</Hash>
              </>
            );
          })}
        </PostUserComment>
      </PostBottom>
    </VideoCards>
  );
};

const Hash = styled.div`
  margin-left: 0.5rem;
  font-family: "AppleSDGothicNeoR";
  color: blue;
`;

const PostBottom = styled.div`
  margin-top: 2rem;
  margin-left: 10px;
  display: flex;
  flex-direction: row;
`;

const PostLikeCnt = styled.div`
  font-family: "AppleSDGothicNeoR";
  display: flex;
  user-select: none;
`;

const PostUser = styled.div`
  display: flex;
  flex-direction: row;
  user-select: none;
`;

const PostUserID = styled.div`
  font-family: "AppleSDGothicNeoB";
  display: flex;
`;

const PostUserComment = styled.div`
  font-family: "AppleSDGothicNeoR";
  display: flex;
  margin-left: 18px;
  cursor: pointer;
`;

const VideoCards = styled.div`
  display: flex;
  flex-direction: column;
  ${(props) => (props.margin ? "margin-right:2.5rem" : "")};
  margin-top: 1rem;
  margin-bottom: 2rem;
`;

const VideoPlay = styled.video`
  display: flex;
  height: 45rem;
  width: 25rem;
  margin: 0 auto;
  object-fit: cover;
`;

const VideoBg = styled.div`
  display: flex;
  height: 45rem;
  width: 25rem;
  border-radius: 20px;
  background: beige;
  margin-top: -25rem;
  z-index: -99;
`;

const Profile = styled.div`
  user-select: none;
  margin-bottom: -3rem;
  display: flex;
  z-index: 5;
`;

const ProfileImg = styled.div`
  --size: 38px;
  width: var(--size);
  height: var(--size);
  border-radius: var(--size);
  background-position: center;
  background-image: url(${(props) => props.src});
  background-size: cover;
  margin-left: 16px;
  display: flex;
`;

const ProfileId = styled.div`
  display: flex;
  font-weight: 600;
  font-size: 1rem;
  margin-left: 11px;
  align-items: center;
  /* background:white; */
  border-radius: 20px;
  width: 100px;
  color: white;
  text-shadow: -1px 0 black, 0 1px black, 1px 0 black, 0 -1px black;
`;

const LikeCov = styled.div`
  display: flex;
  align-items: center;
  cursor: pointer;
  z-index: 10;
  & svg {
    width: 1.5rem;
  }
`;

const CommentCov = styled.div`
  display: flex;
  align-items: center;
  cursor: pointer;
  z-index: 10;
  margin-left: 0.5rem;
  & svg {
    width: 1.5rem;
  }
`;

const LikeComment = styled.div`
  display: flex;
  flex-direction: row;
`;

const BookmarkCov = styled.div`
  display: flex;
  align-items: center;
  cursor: pointer;
  z-index: 10;
  margin-left: 0.5rem;
  & svg {
    width: 1rem;
  }
`;

const BottomCov = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  margin: -2.5rem 1rem auto 2rem;
`;

export default Videom3u8;
