import React, { useEffect, useRef, useState } from "react";
import styled from "styled-components";
import { HeartEmpty, HeartFill } from "media/svg/Svg";
import { CLOSE_MODAL } from "redux/modules/trils";
import { useDispatch, useSelector } from "react-redux";
import Hls from "hls.js";
import ProgressBar from "./ProgressBar";
import { TrilsActions, DELETE_POST } from "redux/modules/trils";
import Swal from "sweetalert2";
import ClearIcon from "@material-ui/icons/Clear";

const TrilsDetail = () => {
  const hls = new Hls();
  const player = useRef(null);
  const info = useSelector((state) => state.trils.detail);
  const dispatch = useDispatch();
  const [completed, setCompleted] = useState(0);
  const [progress, setProgress] = useState(0);
  const [editOn, setEditOn] = useState(false);
  const [tags, setTags] = useState(info.information.hashtag);
  const tagInput = useRef(null);

  const removeTag = (i) => {
    const newTags = [...tags];
    newTags.splice(i, 1);
    console.log(newTags);
    setTags([...newTags]);
  };

  const InputKeyDown = (e) => {
    const val = e.target.value;
    if (e.key === "Enter" && val) {
      if (tags.length === 3) {
        alert("태그는 최대 3개까지 가능합니다.");
        return;
      }
      if (tags.find((tag) => tag.toLowerCase() === val.toLowerCase())) {
        return;
      }
      setTags([...tags, val]);
      tagInput.current.value = null;
    } else if (e.key === "Backspace" && !val) {
      removeTag(tags.length - 1);
    }
  };

  const closeModal = () => {
    dispatch(CLOSE_MODAL());
  };

  const params = {
    src: info.information.videoUrl,
  };

  useEffect(() => {
    if (hls === undefined) {
      return;
    }
    if (Hls.isSupported()) {
      hls.attachMedia(player.current);
      hls.config.debug = false;

      hls.on(Hls.Events.MEDIA_ATTACHED, (event, data) => {
        hls.loadSource(params.src);
      });
      hls.on(Hls.Events.ERROR, (event, data) => {
        if (data.fatal) {
          switch (data.type) {
            case Hls.ErrorTypes.NETWORK_ERROR:
              console.log("네트워크 오류");
              hls.startLoad();
              break;
            case Hls.ErrorTypes.MEDIA_ERROR:
              console.log("미디어 오류");
              hls.recoverMediaError();
              break;
            default:
              hls.destroy();
              break;
          }
        }
      });

      return () => hls.destroy();
    } else if (player.current.canPlayType("application/vnd.apple.mpegurl")) {
      player.current.src = params.src;
      player.current.addEventListener("loadedmetadata", () => {
        player.current.play();
      });
    }
  }, []);

  const videoplay = () => {
    player.current.play();
  };

  const videopause = () => {
    player.current.pause();
  };

  const like = () => {
    dispatch(TrilsActions.send_like(info.information.id, info.member.isLike));
  };

  const del = () => {
    Swal.fire({
      title: "게시글을 삭제하시겠습니까?",
      text: "게시글 삭제 시 다시 복구할 수 없습니다.",
      icon: "warning",
      showCancelButton: true,
      confirmButtonColor: "#3085d6",
      cancelButtonColor: "#d33",
      confirmButtonText: "삭제",
      cancelButtonText: "취소",
    }).then((result) => {
      if (result.isConfirmed) {
        const access_token = localStorage.getItem("access_token");
        const url = `http://13.209.8.146/api/posts/${info.information.id}`;
        const data = {
          method: "DELETE",
          headers: {
            Authorization: `${access_token}`,
          },
        };
        fetch(url, data)
          .then((result) => {
            return result.json();
          })
          .then((result) => {
            if (result.ok) {
              dispatch(DELETE_POST(info.information.id));
              Swal.fire(
                "삭제 완료!",
                "게시글이 삭제되었습니다.",
                "success"
              ).then(() => {
                closeModal();
              });
            }
          })
          .catch((err) => console.log(err));
      }
    });
  };

  const hash = (e) => {
    dispatch(TrilsActions.getPost(e.target.id, "modifiedAt", 1))
    closeModal()
  };

  const edit = () => {
    setEditOn(true);
  };

  const cancelEdit = () => {
    setEditOn(false);
  };

  const doEdit = () => {
    const access_token = localStorage.getItem("access_token");
    const url = `http://13.209.8.146/api/posts/${info.information.id}`;
    const data = {
      method: "PUT",
      headers: {
        Authorization: `${access_token}`,
      },
      body: {
        hashtag: tags,
      },
    };
    fetch(url, data)
      .then((result) => {
        return result.json();
      })
      .then((result) => {
        if (result.ok) {
          console.log(result);
        }
      })
      .catch((err) => console.log(err));
  };

  return (
    <React.Fragment>
      <Component onClick={closeModal} />
      <Wrap>
        <Profile>
          <ProfileImg src={info.author.profileImgUrl} />
          <ProfileId>{info.author.nickname}</ProfileId>
        </Profile>
        <View onMouseOver={videoplay} onMouseLeave={videopause}>
          <VideoPlay
            ref={player}
            muted
            loop
            onTimeUpdate={() => {
              setCompleted(
                (player.current.currentTime / player.current.duration) * 100
              );
              setProgress(player.current.clientWidth);
            }}
          />
        </View>
        <Progress width={progress}>
          <ProgressBar bgcolor={"#6a1b9a"} completed={completed} />
        </Progress>
        <LowWrap>
          <LikeCov onClick={like}>
            {info.member.isLike ? <HeartFill /> : <HeartEmpty />}
          </LikeCov>
          <p style={{ color: "#8B8888", width: "5rem" }}>
            좋아요 +{info.information.likeNum}
          </p>
          <Tag>
            {editOn ? (
              <>
                {tags.map((tag, i) => (
                  <Li key={tag}>
                    #{tag}
                    <Libutton
                      type="button"
                      onClick={() => {
                        removeTag(i);
                      }}
                    >
                      <IconCover>
                        <ClearIcon />
                      </IconCover>
                    </Libutton>
                  </Li>
                ))}
              </>
            ) : (
              <>
                {info.information.hashtag.map((p, idx) => {
                  return (
                    <>
                      <Hash id={p} onClick={hash}>
                        #{p}
                      </Hash>
                    </>
                  );
                })}
              </>
            )}
          </Tag>
        </LowWrap>
        {editOn ? (
          <InputTag>
            <Input
              type="text"
              onKeyDown={InputKeyDown}
              ref={tagInput}
              placeholder="# 자유롭게 적고 엔터를 눌러주세요."
            ></Input>
          </InputTag>
        ) : (
          <></>
        )}
        <Bottom>
          {editOn ? (
            <>
              <Edit onClick={doEdit}>수정완료</Edit>
              <Delete onClick={cancelEdit}>수정취소</Delete>
            </>
          ) : (
            <>
              {info.member.isMembers ? (
                <>
                  <Edit onClick={edit}>수정하기</Edit>
                  <Delete onClick={del}>삭제하기</Delete>
                </>
              ) : (
                <></>
              )}
            </>
          )}
          <Close onClick={closeModal}>닫기</Close>
        </Bottom>
      </Wrap>
    </React.Fragment>
  );
};

