import React, { useRef } from "react";
import styled from "styled-components";

import ximage from "media/image/X.png";
import { Logo } from "media/svg/Svg";

// 메뉴 버튼 열었을 때
const Menu = () => {
  const clickRef = useRef();
  const cancle = () => {};
  return (
    <React.Fragment>
      <Test ref={clickRef}>
        <MenuWrap>
          <RowWrap>
            <div onClick={cancle}>
              <CancleButton />
            </div>

            <LogoWrapper>
              <Logo />
            </LogoWrapper>
          </RowWrap>
          <Line />
          <Category1>Trils</Category1>
          <Br />
          <Category1>Trilog</Category1>
          <Br />
          <Category1>About</Category1>
        </MenuWrap>
      </Test>
    </React.Fragment>
  );
};

export default Menu;

const MenuWrap = styled.div`
  display: none;
  @media (max-width: 540px) {
    display: flex;
    flex-direction: column;
    width: auto;
    height: 50vh;
  }
`;

const RowWrap = styled.div`
  @media (max-width: 540px) {
    display: flex;
    flex-direction: row;
    width: auto;
    padding: 1rem;
  }
`;

const CancleButton = styled.div`
  width: 1rem;
  height: 1rem;
  background-image: url(${ximage});
  background-size: cover;
`;

const LogoWrapper = styled.div`
  margin-left: 12.5rem;
  cursor: pointer;
  & svg {
    width: 6rem;
  }
`;

const Category1 = styled.a`
  cursor: pointer;
  color: #2b61e1;
  opacity: 1;
  padding: 1rem;
`;

const Br = styled.div`
  width: 100%;
  height: 1px;
  background-color: #89acff;
`;

const Line = styled.div`
  width: 100vw;
  height: 5px;
  background: #89acff;
  opacity: 1;
`;

const Test = styled.div`
  position: absolute;
  left: 100rem;
  width: 100%;
  height: 100%;
  background-color: #fff;
  /* z-index: 9999; */
`;
