import ClearIcon from "@material-ui/icons/Clear";
import { config } from "../../redux/modules/config";
import uploading from "../../media/image/uploading.png";

const TrilsDetailTutorialM = (props) => {
  const { history } = props;
  const player = useRef(null);
  const players = useRef(null);
  const info = props.info;
  const dispatch = useDispatch();
  const [completed, setCompleted] = useState(0);
  const [progress, setProgress] = useState(0);
  const [editOn, setEditOn] = useState(false);
  const [tags, setTags] = useState(info.information.hashtag);
  const tagInput = useRef(null);
  const [mute, setMute] = useState(true);
  const [tagType, setTagType] = useState("");
  const [like_chk, setLike] = useState(false);

  const removeTag = (i) => {
    const newTags = [...tags];
    newTags.splice(i, 1);
    setTags([...newTags]);
  };

  const InputKeyDown = (e) => {
    const val = e.target.value;
    if (
      (e.key === "Enter" && val) ||
      (e.key === "," && val) ||
      (e.key === " " && val)
    ) {
      if (tags.length === 3) {
        alert("태그는 최대 3개까지 가능합니다.");
        return;
      }
      if (tags.find((tag) => tag.toLowerCase() === val.toLowerCase())) {
        return;
      }
      setTagType("");
      setTags([...tags, val]);
    } else if (e.key === "Backspace" && !val) {
      removeTag(tags.length - 1);
    }
  };

  const closeModal = () => {
    history.goBack();
  };

  const params = {
    src: info.information.videoUrl,
  };

  useEffect(() => {
    const hls = new Hls();
    if (hls === undefined) {
      return;
    }
    if (Hls.isSupported()) {
      hls.attachMedia(player.current);
      hls.config.debug = false;

      hls.on(Hls.Events.MEDIA_ATTACHED, (event, data) => {
        hls.loadSource(params.src);
      });
      hls.on(Hls.Events.ERROR, (event, data) => {
        if (data.fatal) {
          switch (data.type) {
            case Hls.ErrorTypes.NETWORK_ERROR:
              console.log("네트워크 오류");
              hls.startLoad();
              break;
            case Hls.ErrorTypes.MEDIA_ERROR:
              console.log("미디어 오류");
              hls.recoverMediaError();
              break;
            default:
              hls.destroy();
              break;
          }
        }
      });

      return () => hls.destroy();
    } else if (player.current.canPlayType("application/vnd.apple.mpegurl")) {
      player.current.src = params.src;
      player.current.addEventListener("loadedmetadata", () => {
        player.current.play();
      });
    }
  }, [params.src]);

  const m3u8volume = () => {
    if (player.current.readyState !== 4) {
      return;
    }
    if (!info.information.posPlay) {
      return;
    }
    if (mute) {
      setMute(false);
    } else {
      setMute(true);
    }
  };

  const mp4volume = () => {
    if (players.current.readyState !== 4) {
      return;
    }
    if (!info.information.posPlay) {
      return;
    }
    if (mute) {
      setMute(false);
    } else {
      setMute(true);
    }
  };

  const like = () => {
    if (like_chk) {
      setLike(false);
    } else {
      setLike(true);
    }
  };

  const del = () => {
    Swal.fire({
      title: "사용자 가이드",
      text: "사용자 가이드에서는 삭제 기능이 없습니다.",
      confirmButtonText: "확인",
    });
  };

  const hash = (e) => {
    Swal.fire({
      title: "사용자 가이드",
      text: "해시태그를 클릭하면 해시태그를 검색 할 수 있습니다.",
      confirmButtonText: "확인",
    });
  };

  const edit = () => {
    setEditOn(true);
  };

  const cancelEdit = () => {
    setTags(info.information.hashtag);
    setEditOn(false);
  };

  const doEdit = () => {
    setEditOn(false);
  };

  const change = (e) => {
    if (e.target.value.length > 10) {
      e.target.value = e.target.value.substr(0, 10);
    }
    const curValue = e.target.value;
    const regExp = /[{}[\]/?.,;:|)*~`!^\-_+<>@#$%&\\=('"\s]/gi;
    // const regExp = /[\{\}\[\]\/?.,;:|\)*~`!^\-_+<>@\#$%&\\\=\(\'\"\s]/gi;
    const newValue = curValue.replace(regExp, "");
    setTagType(newValue);
  };

  const hlsplay = () => {
    if (player.current.readyState !== 4) {
      return;
    }
    player.current.play();
  };

  const hlspause = () => {
    if (player.current.readyState !== 4) {
      return;
    }
    player.current.pause();
  };

  const mp4play = () => {
    if (players.current.readyState !== 4) {
      return;
    }
    players.current.play();
  };

  const mp4pause = () => {
    if (players.current.readyState !== 4) {
      return;
    }
    players.current.pause();
  };

  return (
    <React.Fragment>
      <Wrap>
        <Profile>
          <ProfileImg src={info.author.profileImgUrl} />
          <ProfileId>{info.author.nickname}</ProfileId>
        </Profile>
        {info.information.posPlay ? (
          <>
            {info.information.videoType === "mp4" ||
            info.information.videoType === "mov" ? (
              <View
                onMouseOver={mp4play}
                onMouseLeave={mp4pause}
                onClick={mp4volume}
              >
                <VideoPlay
                  ref={players}
                  src={params.src}
                  muted={mute}
                  loop
                  autoPlay
                  onTimeUpdate={() => {
                    setCompleted(
                      (players.current.currentTime / players.current.duration) *
                        100
                    );
                    setProgress(players.current.clientWidth);
                  }}
                />
              </View>
            ) : (
              <>
                <View
                  onMouseOver={hlsplay}
                  onMouseLeave={hlspause}
                  onClick={m3u8volume}
                >
                  <VideoPlay
                    ref={player}
                    muted={mute}
                    loop
                    autoPlay
                    onTimeUpdate={() => {
                      setCompleted(
                        (player.current.currentTime / player.current.duration) *
                          100
                      );
                      setProgress(player.current.clientWidth);
                    }}
                  />
                </View>
              </>
            )}
          </>
        ) : (
          <>
            <View>
              <Uploading src={uploading} />
            </View>
          </>
        )}
        <Progress width={progress}>
          <ProgressBar bgcolor={"#6a1b9a"} completed={completed} />
        </Progress>
        <LowWrap>
          <LikeCov onClick={like}>
            {like_chk ? <HeartFill /> : <HeartEmpty />}
          </LikeCov>
          <LikeText>좋아요 +{info.information.likeNum}</LikeText>
          <Tag>
            {editOn ? (
              <>
                {tags.map((tag, i) => (
                  <Li key={tag}>
                    #{tag}
                    <Libutton
                      type="button"
                      onClick={() => {
                        removeTag(i);
                      }}
                    >
                      <IconCover>
                        <ClearIcon />
                      </IconCover>
                    </Libutton>
                  </Li>
                ))}
              </>
            ) : (
              <>
                {info.information.hashtag.map((p, idx) => {
                  return (
                    <Fragment key={idx}>
                      <Hash id={p} onClick={hash}>
                        #{p}
                      </Hash>
                    </Fragment>
                  );
                })}
              </>
            )}
          </Tag>
        </LowWrap>
        {editOn ? (
          <InputTag>
            <Input
              type="text"
              maxLength={10}
              value={tagType}
              onKeyDown={InputKeyDown}
              ref={tagInput}
              placeholder="# 자유롭게 적고 엔터를 눌러주세요.(10자 제한)"
              onChange={change}
            ></Input>
          </InputTag>
        ) : (
          <></>
        )}
        <Bottom>
          {editOn ? (
            <>
              <Edit onClick={doEdit}>수정완료</Edit>
              <Delete onClick={cancelEdit}>수정취소</Delete>
            </>
          ) : (
            <>
              {info.member.isMembers ? (
                <>
                  <Edit onClick={edit}>수정하기</Edit>
                  <Delete onClick={del}>삭제하기</Delete>
                </>
              ) : (
                <></>
              )}
            </>
          )}
          <Close onClick={closeModal}>뒤로가기</Close>
        </Bottom>
      </Wrap>
    </React.Fragment>
  );
};

