import React, { useState } from "react";
import styled from "styled-components";
import "shared/scss/Category.scss";

import logo from "../media/image/triportlogo.png";

const Header = (props) => {
  const [click, setClick] = useState(false);

  const handleClick = () => {
    setClick(!click);
  };

  if (props.ok === true) {
    return (
      <React.Fragment>
        <Wrap>
          <Logo />
          <LeftWrap>
            <a className="btn" href="/post">
              SNS
            </a>
            <div>게시판</div>
          </LeftWrap>
          <div style={{ width: "30rem" }}></div>
          <RightWrap>
            <Nickname>{props.nickname}</Nickname>
            <MyOrLogin>마이페이지</MyOrLogin>
            <LogoutOrSignUp>로그아웃</LogoutOrSignUp>
          </RightWrap>
        </Wrap>
        <Line />
      </React.Fragment>
    );
  } else {
    return (
      <React.Fragment>
        <Wrap>
          <Logo />
          <LeftWrap>
            {/* <Sns>SNS</Sns>
            <Board>게시판</Board> */}
          </LeftWrap>
          <div style={{ width: "30rem" }}></div>
          <RightWrap>
            <MyOrLogin>로그인</MyOrLogin>
            <LogoutOrSignUp>회원가입</LogoutOrSignUp>
          </RightWrap>
        </Wrap>
        <Line />
      </React.Fragment>
    );
  }
};

Header.defaultProps = {
  ok: true,
};

export default Header;

const Wrap = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-evenly;
  width: 100%;
  height: 5rem;
  background-color: #ffffff;
  background-size: cover;
  align-items: center;
`;

const LeftWrap = styled.div`
  display: flex;
  flex-direction: row;
  font-family: "TTTogether";
`;

const RightWrap = styled.div`
  display: flex;
  flex-direction: row;
  width: auto;
`;

const Line = styled.div`
  width: 100%;
  height: 5px;
  background: #2b61e1 0% 0% no-repeat padding-box;
  opacity: 0.1;
`;

const Logo = styled.div`
  width: 15rem;
  height: 2.5rem;
  background-image: url("${logo}");
  background-size: 15rem 2.5rem;
`;

const Sns = styled.a`
  color: #2b61e1;
  margin-right: 4rem;
  opacity: 0.5;
`;

const Board = styled.li`
  color: #2b61e1;
`;

const MyOrLogin = styled.button`
  background-color: #2b61e1;
  color: #ffffff;
  border: 1px solid #2b61e1;
  border-radius: 5px;
  padding: 0.3rem;
  margin-right: 1.5rem;
  width: 5rem;
  height: 1.7rem;
`;

const LogoutOrSignUp = styled.button`
  background-color: #ffffff;
  border: 1px solid #989898;
  border-radius: 5px;
  padding: 0.3rem;
  width: 5rem;
  height: 1.7rem;
`;

const Nickname = styled.div`
  margin-right: 2rem;
  margin-top: 0.15rem;
`;
