import React, { useEffect, useState, useRef } from "react";
import styled from "styled-components";
import { Logo } from "media/svg/Svg";

import grade1 from "media/svg/등급1.svg";
import grade2 from "media/svg/등급2.svg";
import grade3 from "media/svg/등급3.svg";
import ximage from "media/image/X.png";

import Category from "components/Category";
import { useDispatch, useSelector } from "react-redux";

const Header = (props) => {
  const dispatch = useDispatch();
  const { history } = props;

  const clickRef = useRef();
  const divRef = useRef();

  const handleMenu = () => {
    if (clickRef.current.style.display === "none") {
      clickRef.current.style.display = "block";
      divRef.current.style.display = "none";
    } else {
      clickRef.current.style.display = "none";
      divRef.current.style.display = "block";
    }
  };

  const user = useSelector((state) => state.user);

  const gradeImg = () => {
    if (user.memberGrade === "TRAVELER") {
      return grade1;
    }
    if (user.memberGrade === "TRAVEL_EDITOR") {
      return grade2;
    }
    if (user.memberGrade === "TRAVEL_MASTER") {
      return grade3;
    }
  };

  // 카카오 로그아웃
  const KAKAO_LOGOUT_URL =
    "https://kauth.kakao.com/oauth/logout?client_id=b30e166ade03d146889e1b012679fcf6&logout_redirect_uri=https://triport.kr/auth/logout";
  //"https://kauth.kakao.com/oauth/logout?client_id=b30e166ade03d146889e1b012679fcf6&logout_redirect_uri=http://localhost:3000/auth/logout";

  if (user.is_login === true) {
    return (
      <React.Fragment>
        <div
          style={{
            position: "sticky",
            top: "0",
            zIndex: "50",
            backgroundColor: "#fff",
          }}
        >
          <MobileDiv ref={clickRef}>
            <MenuWrap>
              <RowWrap>
                <div onClick={handleMenu}>
                  <CancleButton />
                </div>

                <LogoWrapper2>
                  <Logo />
                </LogoWrapper2>
              </RowWrap>
              <MenuLine />
              <Category1
                onClick={() => {
                  history.push("/");
                  clickRef.current.style.display = "none";
                }}
              >
                Trils
              </Category1>
              <Br />
              <Category1
                onClick={() => {
                  history.push("trilog");
                  clickRef.current.style.display = "none";
                }}
              >
                Trilog
              </Category1>
              <Br />
              <Category1
                onClick={() => {
                  history.push("/about");
                  clickRef.current.style.display = "none";
                }}
              >
                About
              </Category1>
              <Br />
              <Category1
                onClick={() => {
                  history.push("/profile");
                  clickRef.current.style.display = "none";
                }}
              >
                마이페이지
              </Category1>
              <Br />
              <Category1 href={KAKAO_LOGOUT_URL}>로그아웃</Category1>
              <Br />
            </MenuWrap>
          </MobileDiv>

          <div ref={divRef}>
            <Wrap>
              <Mobile>
                <MenuButton onClick={handleMenu}>
                  <Bar></Bar>
                  <Bar></Bar>
                  <Bar></Bar>
                </MenuButton>
              </Mobile>
              <LogoWrapper
                onClick={() => {
                  const pathname = history.location.pathname;
                  if (pathname === `/`) {
                    history.replace(`/`);
                  } else {
                    history.push(`/`);
                  }
                }}
              >
                <Logo />
              </LogoWrapper>
              <Mobile>
                <Image url={user.profileImgUrl} />
              </Mobile>

              <LeftWrap>
                <Category history={history} />
              </LeftWrap>
              <RightWrap>
                <Image url={user.profileImgUrl} />
                {/* <Grade url={gradeImg} /> */}
                <Nickname>{user.nickname}</Nickname>

                <MyOrLogin
                  onClick={() => {
                    const pathname = history.location.pathname;

                    if (pathname === "/profile") {
                      window.location.reload();
                    } else {
                      history.push("/profile");
                      window.location.reload();
                    }
                  }}
                >
                  마이페이지
                </MyOrLogin>

                {/* 카카오 로그아웃 URL로 이동 -> OAuth2LogoutHandler(카카오 로그아웃, 일반 로그아웃 둘 다 적용) 컴포넌트 로드 */}
                <LogoutOrSignUp href={KAKAO_LOGOUT_URL}>
                  로그아웃
                </LogoutOrSignUp>
              </RightWrap>
            </Wrap>
            <Line />
          </div>
        </div>
      </React.Fragment>
    );
  } else {
    return (
      <React.Fragment>
        <MobileDiv ref={clickRef}>
          <MenuWrap>
            <RowWrap>
              <div onClick={handleMenu}>
                <CancleButton />
              </div>

              <LogoWrapper2>
                <Logo />
              </LogoWrapper2>
            </RowWrap>
            <MenuLine />
            <Category1
              onClick={() => {
                history.push("/");
                clickRef.current.style.display = "none";
              }}
            >
              Trils
            </Category1>
            <Br />
            <Category1
              onClick={() => {
                history.push("/trilog");
                clickRef.current.style.display = "none";
              }}
            >
              Trilog
            </Category1>
            <Br />
            <Category1
              onClick={() => {
                history.push("/about");
                clickRef.current.style.display = "none";
              }}
            >
              About
            </Category1>
            <Br />
            <Category1
              onClick={() => {
                history.push("/login");
                clickRef.current.style.display = "none";
              }}
            >
              로그인 하러가기
            </Category1>
            <Br />
            <Category1
              onClick={() => {
                history.push("/signup");
                clickRef.current.style.display = "none";
              }}
            >
              회원가입 하러가기
            </Category1>
            <Br />
          </MenuWrap>
        </MobileDiv>
        <Wrap>
          <Mobile>
            <MenuButton onClick={handleMenu}>
              <Bar></Bar>
              <Bar></Bar>
              <Bar></Bar>
            </MenuButton>
          </Mobile>
          <LogoWrapper
            onClick={() => {
              const pathname = history.location.pathname;
              if (pathname === "/") {
                window.location.reload();
              } else {
                history.push("/");
              }
            }}
          >
            <Logo />
          </LogoWrapper>
          <Mobile></Mobile>
          <LeftWrap>
            <Category history={history} />
          </LeftWrap>
          <RightWrap>
            <MyOrLogin
              onClick={() => {
                const pathname = history.location.pathname;
                if (pathname === "/login") {
                  window.location.reload();
                } else {
                  history.push("/login");
                }
              }}
            >
              로그인
            </MyOrLogin>
            <LogoutOrSignUp
              onClick={() => {
                const pathname = history.location.pathname;
                if (pathname === "/signup") {
                  window.location.reload();
                } else {
                  history.push("/signup");
                }
              }}
            >
              회원가입
            </LogoutOrSignUp>
          </RightWrap>
        </Wrap>
        <Line />
      </React.Fragment>
    );
  }
};

Header.defaultProps = {
  ok: false,
  nickname: "닉네임예시",
};

export default Header;

