import React, { useState } from "react";
import styled from "styled-components";

import Dmypost from "media/svg/내가 쓴 글 D.svg";

import Video from "components/trils/Video";
import TrilsDetail from "../components/trils/TrilsDetail";
import { BoardCard } from "components/components";

import { history } from "redux/configureStore";
import { useDispatch, useSelector } from "react-redux";
import { actionCreators as profileActions } from "redux/modules/profile";
import { actionCreators as TrilogActions } from "redux/modules/trilog";
import { TrilsActions } from "redux/modules/trils";
import { Fragment } from "react";

const ProfileMyPost = () => {
  const dispatch = useDispatch();
  const mytrils_post = useSelector((state) => state.trils.data);
  const mytrilog_post = useSelector((state) => state.trilog.main.list);
  const [trilsindex, setTrilsIndex] = useState(4);
  const [trilogindex, setTrilogIndex] = useState(5);

  const modal = useSelector((state) => state.trils.modal);

  React.useEffect(() => {
    dispatch(TrilsActions.getMyTrilsPost());
    dispatch(TrilogActions.getTrilogMainMyPage());
  }, []);

  return (
    <React.Fragment>
      {modal ? <TrilsDetail history={history} /> : null}
      <ColumnWrap>
        <Wrap>
          <ColumnWrap>
            <Div>
              <Title>Trils</Title>
              <Button
                onClick={(e) => {
                  e.target.style.display = "none";
                  setTrilsIndex(mytrils_post.length);
                }}
              >
                더보기
              </Button>
            </Div>
            <Postlist>
              {!mytrils_post || mytrils_post.length === 0 ? (
                <Text>내 Trils가 없습니다.</Text>
              ) : (
                <>
                  {mytrils_post.map((p, idx) => {
                    if (idx <= trilsindex) {
                      if ((idx + 1) % 4 !== 0) {
                        return (
                          <Fragment key={idx}>
                            <Video {...p} history={history} />
                          </Fragment>
                        );
                      }
                    }
                  })}
                </>
              )}
            </Postlist>
            <Br />
          </ColumnWrap>
        </Wrap>

        <ColumnWrap>
          <Wrap style={{ marginBottom: "5rem" }}>
            <ColumnWrap>
              <Div>
                <Title>Trilog</Title>
                <Button
                  onClick={(e) => {
                    e.target.style.display = "none";
                    setTrilogIndex(mytrilog_post.length);
                  }}
                >
                  더보기
                </Button>
              </Div>
              <Postlist>
                {!mytrilog_post || mytrilog_post.length === 0 ? (
                  <Text>내 Trilog가 없습니다.</Text>
                ) : (
                  <>
                    {mytrilog_post.map((val, idx) => {
                      const index = idx + 1;
                      if (idx <= trilogindex) {
                        if (index % 5 === 0) {
                          return (
                            <BoardCardDiv key={index}>
                              <BoardCard
                                data={val}
                                margin="50px 20px 0 0"
                              />
                            </BoardCardDiv>
                          );
                        } else {
                          return (
                            <BoardCardDiv key={index}>
                              <BoardCard
                                data={val}
                                margin="50px 20px 0 0"
                              />
                            </BoardCardDiv>
                          );
                        }
                      }
                    })}
                  </>
                )}
              </Postlist>
            </ColumnWrap>
          </Wrap>
        </ColumnWrap>
      </ColumnWrap>
    </React.Fragment>
  );
};

export default ProfileMyPost;

const Wrap = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  font-family: "paybooc-Bold";
  width: 100vw;
`;

const ColumnWrap = styled.div`
  display: flex;
  flex-direction: column;
  width: 100%;
`;

const Div = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  width: 90%;
  margin-left: 5rem;
  @media (max-width: 540px) {
    margin-left: 1rem;
  }
`;

const Icon = styled.div`
  width: 2rem;
  height: 1.3rem;
  background-image: url("${Dmypost}");
  background-size: 2rem 1.3rem;
  margin-left: 5rem;
`;

const Title = styled.div`
  width: 8rem;
  color: #2b61e1;
  margin-left: 1rem;
  margin-bottom: 3rem;
  font-size: 1.2rem;

  @media (max-width: 1270px) {
    font-size: 1rem;
    width: 6rem;
  }
`;

const Button = styled.button`
  width: 3.5rem;
  height: 1.5rem;
  border: 1px solid #989898;
  border-radius: 5px;
  background-color: #ffffff;
  padding: 0.2rem;
  margin-right: 5rem;
  cursor: pointer;
  @media (max-width: 540px) {
    margin-right: 2rem;
  }
`;

const Br = styled.div`
  width: 90%;
  height: 3rem;
  margin-bottom: 3rem;
  border-bottom: 3px solid #89acff;
  margin-left: 5rem;
  @media (max-width: 540px) {
    margin-left: 2rem;
    width: 85%;
  }
`;

const Postlist = styled.div`
  display: grid;
  column-gap: 2rem;
  grid-template-columns: 1fr 1fr 1fr;
  flex-wrap: wrap;
  display: flex;
  flex-direction: row;
  width: 90%;
  margin-left: 5rem;

  @media (max-width: 540px) {
    width: 95%;
    margin-left: 2rem;
    justify-content: center;
  }
  @media (max-width: 375px) {
    margin-left: 1rem;
    width: 90%;
    justify-content: center;
  }
`;

const Text = styled.div`
  margin-left: 6rem;
`;

const BoardCardDiv = styled.div`
  @media (max-width: 540px) {
    transform: scale(0.7);
  }
  @media (max-width: 375px) {
    transform: scale(1);
  }
`;
